import Axios from "axios";
import React from "react";
import "./ProblemArea.css";
import ShowTcInp from "./ShowTcInp";
import ShowTcOut from "./ShowTcOut";
// import Iframe from "react-iframe";

function ProblemArea() {
  const data = {
    type: "problem",
    id: 3,
  };
  const showTCIn = (tc) => {
    var inps = new Array();
    for (var i = 1; i <= tc; i++) {
      inps.push(
        "http://localhost:8000/media/TestCases/" +
          data.id.toString() +
          "/sc-input" +
          i.toString() +
          ".txt"
      );
    }
    const renderinp = inps.map(inp => {
      return(
        <Iframe
        id="frmFile"
        src="http://localhost:8000/media/TestCases/3/sc-input1.txt"
        onLoad={LoadFile}
      ></Iframe>
      )
    });
    console.log(inps);
  };

  const showTCOt = (tc) => {
    var ops = new Array();
    for (var i = 1; i <= tc; i++) {
      ops.push(
        "http://localhost:8000/media/TestCases/" +
          data.id.toString() +
          "/sc-output" +
          i.toString() +
          ".txt"
      );
    }
    console.log(ops);
  };

  const getData = async (data) => {
    const response = await Axios.post(" http://localhost:8000/get/", data);
    const out = response.data;
    document.querySelector(".problemTitle").innerHTML = out["title"];
    document.querySelector(".problemStatement").innerHTML = out["description"];
    // const sampleTC = out["sampleTc"];
    // console.log(sampleTC);
  };

  getData(data);

  // const LoadFile = () => {
  //   var oFrame = document.getElementById("frmFile");
  //   var strRawContents =
  //     oFrame.contentWindow.document.body.childNodes[0].innerHTML;
  //   while (strRawContents.indexOf("\r") >= 0)
  //     strRawContents = strRawContents.replace("\r", "");
  //   var arrLines = strRawContents.split("\n");
  //   alert("File " + oFrame.src + " has " + arrLines.length + " lines");
  //   for (var i = 0; i < arrLines.length; i++) {
  //     var curLine = arrLines[i];
  //     alert("Line #" + (i + 1) + " is: '" + curLine + "'");
  //   }
  // };

  return (
    <div>
      <h1 className="problemTitle"></h1>
      <br></br>
      <p className="problemStatement"></p>
      <br></br>
      <h3 className="TestCaseHeading">Sample Test Cases</h3>
      <div className="ui segment" id="testCases">
        <div className="ui two column very relaxed grid">
          <div className="column" id="testCasesColumn">
            <h3>Input</h3>
            {showTCIn(2)}
          </div>
          <div className="column" id="testCasesColumn">
            <h3>Output</h3>
            <pre>{showTCOt(2)}</pre>
          </div>
        </div>
        <div className="ui vertical divider"></div>
      </div>
      <p>Time Limit : 1</p>
      <p>Memory Limit: 1</p>
    </div>
  );
}

export default ProblemArea;
