import React from "react";

function LanguageDrop(props) {
  var LangMapping = {
    "C++17": "cpp17",
    "C++14": "cpp14",
    "C" : "c",
    "Python3": "p3",
    "Python2": "p2",
    "Java":  "java",
    "JavaScript" : "javascript",
  };

  const LangHelper = (e) => {
    const newId = LangMapping[e.target.text];
    const currId = props.props;
    props.dispatch(e.target.text);
    console.log(currId);

    document.getElementById(currId).className = "dropdown-item";
    document.getElementById(newId).className = "dropdown-item active";
  };

  return (
    <div>
      <div className="dropdown">
        <button
          className="btn btn-secondary dropdown-toggle"
          type="button"
          id="dropdownMenuButton"
          data-toggle="dropdown"
          aria-haspopup="true"
          aria-expanded="false"
        >
          Select Language
        </button>
        <div
          className="dropdown-menu"
          aria-labelledby="dropdownMenuButton"
          onClick={(e) => LangHelper(e)}
        >
          <a id="cpp17" className="dropdown-item active" href="/#">
            C++17
          </a>
          <a id="cpp14" className="dropdown-item" href="/#">
            C++14
          </a>
          <a id="c" className="dropdown-item" href="/#">
            C
          </a>
          <a id="p3" className="dropdown-item" href="/#">
            Python3
          </a>
          <a id="p2" className="dropdown-item" href="/#">
            Python2
          </a>
          <a id="java" className="dropdown-item" href="/#">
            Java
          </a>
        </div>
      </div>
    </div>
  );
}

export default LanguageDrop;
