import React from "react";

function OutputField(props) {
  var size;
  if (props.currState === "true") {
    size = "20vw";
  } else {
    size = "45vw";
  }

  return (
    <div>
      <h3>Output</h3>
      <textarea
        disabled
        rows="7"
        style={{ width: size }}
        placeholder={props.val}
        onChange={(e) => props.dispatch(e.target.value)}
        id="outputfield"
      ></textarea>
    </div>
  );
}

export default OutputField;
